package dasar.condition;

public class KelasElseIfDasarInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Day = 0;

		if (Day < 1) {
			System.out.println("Hari ini");
		} else if (Day < 2) {
			System.out.println("Hari itu");
		} else {
			System.out.println("Gak ada hari");
		}
	}

}
