package dasar.condition;

public class KelasIfElseDasarString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String cuaca = "Mendung";
		
		if (cuaca.contentEquals("Mendung")) {
			System.out.println("Tidak Banjir");
		} else {
			System.out.println("Ada Kemungkinan Banjir");
		}
	}

}
