package dasar.condition;

public class KelasSwitchDasar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int day = 5; 
		
		switch (day) {
		case 1:
			System.out.println("makan bareng");
			break;
		case 4:
			System.out.println("ngantor");
			break;
		case 5:
			System.out.println("nonton");
			break;
		default:
			System.out.println("gabut");
			break;
		}

	}

}
