package dasar.operator;

public class KelasOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Pertambahan int + Long
		int ia = 10;
		long ba = 9L;
		Long a = ia +ba;
		System.out.println(a);
		
		// Pertambahan int + Double
		int io = 97;
		double fa = 50.20;
		double hasil2 = io + fa;
		System.out.println(hasil2);
		
		// Pertambahan String + char
		String test = "saya";
		char test2 = 'A';
		String test3 = test + test2;
		System.out.println(test3);
		
		
		// Pengurangan Double - float
		double da = 80.9;
		Float di = 70f;
		double hasil3 = da -di;
		System.out.println(hasil3);
		
	
	}

}
